mapper.insert:
insert into ${table} ( b_no, subject, content, pwd, read_count, reg_date, p_no,
rec_count
price , trade_type
) values ( (select nvl(max(b_no),0)+1 from ${table}), #{subject}, #{content}, #{pwd}, 0, sysdate, 1,
0
#{price} , #{trade_type}
)

mapper.update:
update
	${table}
set
	subject = #{subject}
	,content = #{content}
	
	,price = #{price}
	
where
	b_no = #{b_no}
	and
	pwd= #{pwd}

mapper.delete:
delete from 
	${table}
where 
	b_no=#{b_no}
	and
	pwd=#{pwd}

mapper.insert:
insert into freelancer(
    b_no
    ,p_no
    ,preferred_work
    ,start_time
    ,end_time
    ,content
    ,reg_date
    ,pwd    
)values(
   (select nvl(max(b_no),0)+1 from freelancer)
    ,3
    ,#{preferred_work}
    ,#{start_time}
    ,#{end_time}
    ,#{content}
    ,sysdate
    ,#{pwd}
)

mapper.update:
update 
	freelancer
set	
	preferred_work   =  #{preferred_work}
	,start_time      =  #{start_time}
	,end_time        =  #{end_time}
	,content		 =  #{content}
where 
	b_no=#{b_no}
	and
	pwd=#{pwd}

mapper.delete:
delete from 
	freelancer
where 
	b_no=#{b_no}
	and
	pwd=#{pwd}

mapper.insert:
