    b_no
	,p_no
    ,hope_work
    ,start_time
    ,end_time
    ,content
    ,reg_date
    ,pwd
)
values(
    (select nvl(max(b_no),0)+1 from part_time)
    ,1
    ,#{hope_work}
    ,#{start_time}
    ,#{end_time}
    ,#{content}
    ,sysdate
    ,#{pwd}
)

