	and
	pwd=#{pwd}

mapper.insert:
insert into resume(
	resume_no
    ,p_no
    ,married
    ,military
    ,introduce
    ,is_offer
    ,hope_salary 
) values (
(select nvl(max(resume_no),0)+1 from resume)           
     ,1
     ,#{married}
    ,#{military}
    ,#{introduce}
    ,#{is_offer}
    ,#{hope_salary}
)

	insert into ${table}
	(
	comment_no
	,b_no
	,p_no
	,content
	)
	values(
	(select nvl(max(comment_no),0)+1 from ${table})
	,#{b_no}
    ,#{p_no}
    ,#{content}
	)

mapper.update:
update
	${table}
set
	rec_count = rec_count+1
where
	comment_no = #{comment_no}
