mapper.select:
select c.comment_no ,p.nickname , to_char(c.reg_date,'YYYY-MM-DD') as "reg_date" , c.content ,c.rec_count from person_mem p, ${comment} c where c.p_no = p.p_no and c.b_no = #{b_no}
order by ${sort}
order by reg_date asc

mapper.update:
update
	${tablename}
set 
	read_count = read_count + 1
where 
	b_no=#{b_no}

mapper.select:
select count(*) from ${boardname}

 	where ( 
			upper(subject) like upper('%${keyword}%')
			or
			upper(content) like upper('%${keyword}%')
			)

select comment_no as "like_no" from like_comment where comment_table=#{comment}
and p_no = #{p_no}
and c_no = #{c_no}
and b_no=#{b_no} order by 1 asc
