mapper.select:
select * from ( select rownum "RNUM" , zxcvb.* from ( select b.b_no, b.subject, p.nickname, to_char(b.reg_date,'YYYY-MM-DD') "reg_date", b.read_count,
b.rec_count,
b.content
,b.trade_type ,b.price
from ${boardname} b , person_mem p where 1=1 and p.p_no=b.p_no
and ( upper(b.subject) like upper('%${keyword}%') or upper(b.content) like upper('%${keyword}%') )
and trade_type = #{tradetype}
order by reg_date desc
)zxcvb) where RNUM >= #{begin_rowNo} and RNUM <= #{end_rowNo}

select * from ( select rownum "RNUM" , zxcvb.* from ( select ci.c_no ,name ,LISTAGG(wc.welfare_name,',') WITHIN GROUP (ORDER BY wc.welfare_code) AS welfare ,business_industry as "indus" ,sales ,sal_avg ,round(nvl(AVG(r.star),0),0) as "star_avg" ,addr ,ci.rec_count from company_mem c
join company_info ci on c.c_no = ci.c_no left join company_welfare cw on ci.c_no=cw.c_no left join code_welfare wc on cw.welfare_code=wc.welfare_code left join review r on c.c_no = r.c_no
where 1=1
and ( upper(addr) like upper('%${sido}%') )
and ( upper(addr) like upper('%${gugun}%') )
and business_industry = #{selectedIndustry}
and ( upper(name) like upper('%${keyword}%') )
GROUP BY ci.c_no,name,business_industry,sales,sal_avg,addr,ci.rec_count
having
LISTAGG(wc.welfare_name, ',') WITHIN GROUP (ORDER BY wc.welfare_name) like '%${tmp}%'
order by ${sort}
order by ${multisort1}
${multisort2}
${multisort3}
)zxcvb) where RNUM >= #{begin_rowNo} and RNUM <= #{end_rowNo}

select c.comment_no ,p.p_no ,p.nickname , to_char(c.reg_date,'YYYY-MM-DD') as "reg_date" , c.content ,c.rec_count from person_mem p, ${comment} c where c.p_no = p.p_no and c.b_no = #{b_no}
order by ${sort}
order by reg_date asc

mapper.update:
update
	${tablename}
set 
	read_count = read_count + 1
where 
	b_no=#{b_no}

mapper.select:
select count(*) from ${boardname}
	where
		1=1

 	and ( 
			upper(subject) like upper('%${keyword}%')
			or
			upper(content) like upper('%${keyword}%')
			)
 	
 	
 	and
 		trade_type=#{tradetype}

select comment_no as "like_no" from like_comment where comment_table=#{comment}
and p_no = #{p_no}
and c_no = #{c_no}
and b_no=#{b_no} order by 1 asc

select * from ( select rownum "RNUM" , zxcvb.* from ( select category, n_no, subject, content, reg_date, read_count from notice where 1=1 and category='main'
order by reg_date desc
)zxcvb) where RNUM >= #{begin_rowNo} and RNUM <= #{end_rowNo}
