mapper.select:
select * from ( select rownum RNUM, zxcvb.* from( select b_no ,p.p_no ,p.name ,p.phone ,p.addr ,t.subject ,t.hope_work ,t.start_time ,t.end_time ,to_char(t.start_date, 'YYYY-MM-DD') as "start_date" ,to_char(t.end_date, 'YYYY-MM-DD ') as "end_date" ,to_char(t.reg_date, 'YYYY-MM-DD') as "reg_date" from person_mem p , part_time t where p.p_no = t.p_no
and to_number(to_char(t.end_date,'YYYYMMDD')) - to_number(to_char(sysdate,'YYYYMMDD'))>0 and
to_number(to_char(t.start_date,'YYYYMMDD')) - to_number(to_char(sysdate,'YYYYMMDD'))<0
and ( upper(name) like upper('%${keyword}%') or upper(phone) like upper('%${keyword}%') or upper(subject) like upper('%${keyword}%') or upper(addr) like upper('%${keyword}%') )
and( upper(addr) like upper('%${work_place}%') )
order by b_no desc
order by ${sort}
) zxcvb) where RNUM >= #{begin_rowNo} and RNUM <= #{end_rowNo}
