mapper.select:
select * from ( select rownum RNUM, zxcvb.* from (
select b_no ,p.p_no ,p.name ,p.phone ,p.addr ,f.subject ,f.preferred_work ,f.start_time ,f.end_time ,to_char(f.start_date, 'YYYY-MM-DD') as "start_date" ,to_char(f.end_date, 'YYYY-MM-DD') as "end_date" ,to_char(f.reg_date, 'YYYY-MM-DD') as "reg_date" from person_mem p, freelancer f where p.p_no= f.p_no
and to_number(to_char(f.end_date,'YYYYMMDD')) - to_number(to_char(sysdate,'YYYYMMDD'))>0 and
to_number(to_char(f.start_date,'YYYYMMDD')) - to_number(to_char(sysdate,'YYYYMMDD'))<0
and ( upper(name) like upper('%${keyword}%') or upper(phone) like upper('%${keyword}%') or upper(preferred_work) like upper('%${keyword}%') or upper(subject) like upper('%${keyword}%') )
and( upper(addr) like upper('%${work_place}%') )
order by b_no desc
order by ${sort}
) zxcvb) where RNUM >= #{begin_rowNo} and RNUM <= #{end_rowNo}
